// 该文件用于创建 Vuex 中最为核心的 store

// 引入 Vue 核心库
import Vue from 'vue'
// 引入 Vuex
import Vuex from 'vuex'
// 应用 Vuex 插件
Vue.use(Vuex)

// 准备actions————用于响应组建中的动作
const actions = {}
// 准备mutations————用于操作数据（state）
const mutations = {}
// 准备state————用于存储数据
const state = {}


// 创建并暴露（导出） store
export default new Vuex.Store({
  actions,
  mutations,
  state,
})
