// 引入 Vue
import Vue from 'vue'
// 引入 App
import App from './App.vue'
// 引入插件
import vueResource from 'vue-resource'
// 引入 Vuex
import Vuex from 'vuex'
// 引入 store
import store from ''


// 关闭 Vue 的生产提示
Vue.config.productionTip = false
// 使用插件
Vue.use(vueResource)
Vue.use(Vuex)

// 创建 vm
new Vue({
  el: '#app',
  render: h => h(App),
  beforeCreate() {
    Vue.prototype.$bus = this
  }
})
